import { useDataLayerValue } from '../../src/DataLayer'
import { Chrono } from "react-chrono";

import { Chart as ChartJS } from 'chart.js/auto'
import { Chart }            from 'react-chartjs-2'

/*
Homepage: consists of sidebar, favorite artists, and favorite tracks. 
Body component contains favorite artist and favorite tracks.
*/

function Analytics() {

    // access user playlist from the data layer
    const [{ user_playlists, top_track }, dispatch] = useDataLayerValue();

    const items = [{
        title: "May 1940",
        cardTitle: "Dunkirk",
        cardSubtitle:"Men of the British Expeditionary Force (BEF) wade out to..",
        cardDetailedText: "Men of the British Expeditionary Force (BEF) wade out to..",
        media: {
        type: "IMAGE",
        source: {
            url: "http://someurl/image.jpg"
        }
        }
    }, {
        title: "May 1950",
        cardTitle: "Dunkirk",
        cardSubtitle:"Men of the British Expeditionary Force (BEF) wade out to..",
        cardDetailedText: "Men of the British Expeditionary Force (BEF) wade out to..",
        media: {
            type: "IMAGE",
            source: {
            url: "http://someurl/image.jpg"
            }
        }
        }];

    return (

        <div className="homepage">
            <Sidebar />
            <div style={{ width: "1000px", height: "2000px", display: "flex", }}>
            <Chrono items={items} mode="HORIZONTAL" cardHeight={50} cardWidth={200}/>
