import React from 'react';
import {Bar} from 'react-chartjs-2';
import Sidebar from './Sidebar'
import { useDataLayerValue } from '../../src/DataLayer'
import { Chart as ChartJS } from 'chart.js/auto'
import { Chart }            from 'react-chartjs-2'

/*
Homepage: consists of sidebar, favorite artists, and favorite tracks. 
Body component contains favorite artist and favorite tracks.
*/

function Analytics() {

    // access user playlist from the data layer
    const [{ user_playlists, top_track }, dispatch] = useDataLayerValue();

    const playlist_names = user_playlists?.items?.map((playlist) => (playlist.name));
    const playlist_cnts = user_playlists?.items?.map((playlist) => (playlist.tracks.total));

    console.log("sad", user_playlists, top_track)

    // bar chart to display count of tracks in users' playlists
    const playlist_cnt = {
        labels: playlist_names,
        datasets: [
        {
            label: 'Number of tracks in your playlists',
            backgroundColor: 'rgba(75,192,192,1)',
            borderColor: 'rgba(0,0,0,1)',
            borderWidth: 2,
            data: playlist_cnts
        }
        ]
    }


    return (

        <div>
            <Sidebar />
            {user_playlists ? <Bar
                data={playlist_cnt}
                options={{
                    title:{
                    display:true,
                    text:'Number of tracks in your playlists',
                    fontSize:20
                    },
                    legend:{
                    display:false,
                    }
                }}
            /> : <p>You don't have any playlists yet!</p>}

        </div>

    )

}

export default Analytics
