import React, { useState } from 'react';
import {Bar, Line } from 'react-chartjs-2';
import Sidebar from './Sidebar'
import { useDataLayerValue } from '../../src/DataLayer'
import { Chrono } from "react-chrono";

import { Chart as ChartJS } from 'chart.js/auto'
import { Chart }            from 'react-chartjs-2'

/*
Homepage: consists of sidebar, favorite artists, and favorite tracks. 
Body component contains favorite artist and favorite tracks.
*/

function Analytics() {

    // access user playlist from the data layer
    const [{ user_playlists, top_track }, dispatch] = useDataLayerValue();

    // map 




    // console.log("sorted_dates", sortedStrings);

    // a timeline of the user's top tracks

    const num_tracks = top_track?.items.length;

    var sorted_track_dates = top_track?.items?.map((track) => (track.album.release_date)).sort((a,b) => {
        var aComps = a.split("-");
        var bComps = b.split("-");
        var aDate = new Date(aComps[0], aComps[1], aComps[2]);
        var bDate = new Date(bComps[0], bComps[1], bComps[2]);
        return aDate.getTime() - bDate.getTime();
    });

    var sorted_tracks_by_date = new Array(num_tracks);

    for (let i=0; i < num_tracks; i++) {
        var curr_track = top_track?.items[i]
        var curr_track_date = curr_track.album.release_date;
        var idx = sorted_track_dates.indexOf(curr_track_date);
        sorted_tracks_by_date[idx] = curr_track

    }


    // var top_tracks_timeline = new Array(num_tracks);

    const top_tracks_timeline = sorted_tracks_by_date.map((track) => ({
        title: track?.album?.release_date, 
            cardTitle: track?.name,
            media: {
                type: "IMAGE",
                source: {
                    url: track?.album?.images[0].url
                }
            }
    }))


    return (

        <div className="homepage">
            <Sidebar />
            <div style={{ width: "1500px", height: "2000px"}}>
            <Chrono items={top_tracks_timeline} mode="HORIZONTAL" cardHeight={50} cardWidth={200} itemWidth={800}/>
            </div> 
            <Bar
                data={{
                    labels: user_playlists?.items?.map((playlist) => (playlist.name)),
                    datasets: [
                    {
                        label: 'Number of tracks your playlists',
                        backgroundColor: 'rgba(75,33,121,1)',
                        borderColor: 'rgba(0,0,0,1)',
                        borderWidth: 2,
                        data: user_playlists?.items?.map((playlist) => (playlist.tracks.total)),

                    }
                    ]
                }}
            />

        </div>

    )

}

export default Analytics
