import React from "react";
import "./Sidebar.css"
import SidebarOptions from "./SidebarOptions";
import HomeIcon from '@mui/icons-material/Home';
import AnalyticsIcon from '@mui/icons-material/Analytics';
import { useDataLayerValue } from '../../src/DataLayer';

function Sidebar() {

    const [{ location }, dispatch] = useDataLayerValue();

    return (
        <div className="sidebar">
            {/* TODO: replace this with logo */}
            <h1>Sidebar</h1>

            {/* TODO: add icons */}
            <button onClick={
                dispatch({ type: "CHANGE_LOCATION", homepage: true})
                }>
                    <SidebarOptions Icon={HomeIcon} title="Home" />
            </button>
            <button onClick={
                dispatch({ type: "CHANGE_LOCATION", homepage: false})
            }>
            <SidebarOptions Icon={AnalyticsIcon} title="Analytics" />
            </button>

        </div>
    );
}

export default Sidebar;
